        [SMCAuthorize(UC_ORG_001_01_02.MANTER_MANTENEDORA)]
        [HttpPost]
        public ActionResult SalvarProrrogacaoAlunos(ProrrogarAlunosViewModel model)
        {
            var seqsConflitantes = SolicitacaoServicoService.CriarSolicitacoesProrrogacaoManual(model.CodigosMigracao.Select(i => i.CodigoMigracao.Value).ToList());
            if (seqsConflitantes != null && seqsConflitantes.Any())
                SetSuccessMessage($"Prorrogação(ões) efetuada(s) com sucesso, entretanto, o(s) seguinte(s) item(ns) não foi(ram) criado(s) pois já existe(m) solicitação(ões) de prorrogação em aberto: {string.Join(", ", seqsConflitantes)}", "Sucesso");
            else
                SetSuccessMessage("Prorrogação(ões) efetuada(s) com sucesso para todos os itens informados.", "Sucesso");
            return SMCRedirectToAction("ProrrogarAlunos");
        }
